definition.script:
pipeline{
  agent none
  stages {
    stage ('Checkout') {
      agent {
        label 'master'
      }
      steps {
        checkout([
          $class: 'GitSCM',
          branches: [[name: '*/readyPipeDeclScript/*']],
          userRemoteConfigs: [[
            name: 'origin',
            url: 'git@github.com:bicschneider/test-git-phlow-plugin.git'
          ]],
          extensions: [
            pretestedIntegration(
              gitIntegrationStrategy: accumulated(),
              integrationBranch: 'masterPipeDeclScript',
              repoName: 'origin')
            ,
            [ $class: 'ChangelogToBranch',
              options: [compareRemote: 'origin', compareTarget: 'masterPipeDeclScript']
            ]
          ]
        ])
      post {
        always {
          pretestedIntegrationPublisher()
        }
      }
    }
  }
}
